getPlaying = function(a)
    local i = 0
    if a == nil then
        for name in next, players do
            if players[name].playing then
                i = i + 1
            end
        end
    else
        for name in next, tfm.get.room.playerList do
            if not tfm.get.room.playerList[name].isDead and players[name].joined then
                i = i + 1
            end
        end
    end
    return i
end

