    if gameStarted then
        players[name].playing = false
        if getPlaying() == 1 then
            tfm.exec.newGame(maps[1])
            for name in next, players do
                if players[name].playing then
                    tfm.exec.setPlayerScore(name, 10, true)
                    playerWon = name
