            img,
            hasBarrier = false,
            barrierId,
            barrierPutted = false,
            playerSize = 1
        },
        inventory = {true, false, false, false, false, false},
        imgs = {aliveMice, shopImg, helpImg, shopUiImg, shopMoneyImgi, shopLeftArrow, shopRightArrow},
        inShop = {},
        bag = {
            silver = false,
            orange = false,
            bear = false,
            hamburger = false,
            golden = false,
            pokeball = false,
            sheep = false,
            chicken = false,
